import React from 'react';
import { Switch, Route } from 'react-router-dom';
import loadable from '@loadable/component';

// import { About } from 'routes/About';
// import { Topics } from 'routes/Topics';
// import { Home } from 'routes/Home';
// import { CourseList } from 'routes/Courses/CourseList';
// import { Calendar } from 'routes/Calendar/Calendar';
// import { SignIn } from 'routes/SignIn';
// import { SignUp } from 'routes/SignUp';

function Fallback(): JSX.Element {
  return <div>Loading...</div>;
}

const About = loadable(() => import(`routes/About`), {
  fallback: <Fallback />,
});
const Topics = loadable(() => import(`routes/Topics`), {
  fallback: <Fallback />,
});
const Home = loadable(() => import(`routes/Home`), {
  fallback: <Fallback />,
});
const CourseList = loadable(() => import(`routes/Courses/CourseList`), {
  fallback: <Fallback />,
});
const Calendar = loadable(() => import(`routes/Calendar/Calendar`), {
  fallback: <Fallback />,
});
const SignIn = loadable(() => import(`routes/SignIn`), {
  fallback: <Fallback />,
});
const SignUp = loadable(() => import(`routes/SignUp`), {
  fallback: <Fallback />,
});

export function RoutesContainer(): JSX.Element {
  return (
    <Switch>
      <Route path="/courses">
        <CourseList />
      </Route>
      <Route path="/about">
        {/* <AsyncPage page="About" /> */}
        <About />
      </Route>
      <Route path="/topics">
        {/* <AsyncPage page="Topics" /> */}
        <Topics />
      </Route>{' '}
      <Route path="/calendar">
        <Calendar />
      </Route>
      <Route path="/signin">
        <SignIn />
      </Route>
      <Route path="/signup">
        <SignUp />
      </Route>
      <Route path="/home">
        {/*<AsyncPage page="Home" />*/}
        <Home />
      </Route>
      <Route path="/" exact>
        <CourseList />
      </Route>
    </Switch>
  );
}
