import React from 'react';
import { Switch, Route } from 'react-router-dom';
import { About } from 'routes/About';
import { Topics } from 'routes/Topics';
import { Home } from 'routes/Home';
import { CourseList } from 'routes/Courses/CourseList';
import { Calendar } from 'routes/Calendar/Calendar';
import { SignIn } from 'routes/SignIn';
import { SignUp } from 'routes/SignUp';

export function RoutesContainer(): JSX.Element {
  return (
    <Switch>
      <Route path="/courses">
        <CourseList />
      </Route>
      <Route path="/about">
        <About />
      </Route>
      <Route path="/topics">
        <Topics />
      </Route>{' '}
      <Route path="/calendar">
        <Calendar />
      </Route>
      <Route path="/signin">
        <SignIn />
      </Route>
      <Route path="/signup">
        <SignUp />
      </Route>
      <Route path="/home">
        <Home />
      </Route>
      <Route path="/" exact>
        <CourseList />
      </Route>
    </Switch>
  );
}
